import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './DoctorDashboard.css';
import DoctorProfile from './DoctorProfile';
import AppointmentList from './AppointmentList';
import AppointmentCard from './AppointmentCard';
import AppointmentDetails from './AppointmentDetails';
import AppointmentActions from './AppointmentActions';

const DoctorDashboard = () => {
  const [doctor, setDoctor] = useState(null);
  const [appointments, setAppointments] = useState([]);
  const [selectedAppointment, setSelectedAppointment] = useState(null);

  useEffect(() => {
    // Fetch doctor profile data from the backend
    axios.get('http://localhost:9096/api/doctors/2')
      .then(response => {
        setDoctor(response.data);
      })
      .catch(error => {
        console.error('Error fetching doctor profile:', error);
      });

    // Fetch doctor's appointments from the backend
    axios.get('http://localhost:9096/api/appointments/doctor/2')
      .then(response => {
        setAppointments(response.data);
      })
      .catch(error => {
        console.error('Error fetching appointments:', error);
      });
  }, []);

  const handleAppointmentClick = (appointment) => {
    setSelectedAppointment(appointment);
  };

  const handleAppointmentUpdate = (appointmentId, status) => {
    // Update the appointment status in the frontend state
    const updatedAppointments = appointments.map(appointment => {
      if (appointment.id === appointmentId) {
        return { ...appointment, status };
      }
      return appointment;
    });

    setAppointments(updatedAppointments);
  };

  return (
    <div className="doctor-dashboard">
      <nav className="navbar">
        <ul className="navbar-nav">
          <li className="nav-item">
            <a href="/doctorprofile">DoctorProfile</a>
          </li>
          <li className="nav-item">
            <a href="/appointmentlist">AppointmentList</a>
          </li>
          <li className="nav-item">
            <a href="/appointmentcard">AppointmentCard</a>
          </li>
          <li className="nav-item">
            <a href="/appointmentdetails">AppointmentDetails</a>
          </li>
          <li className="nav-item">
            <a href="/appointmentactions">AppointmentActions</a>
          </li>
        </ul>
      </nav>
      <div className="dashboard-content">
        {doctor && selectedAppointment && (
          <>
            <DoctorProfile doctor={doctor} />
            <AppointmentList
              appointments={appointments}
              onAppointmentClick={handleAppointmentClick}
            />
            <AppointmentCard appointment={selectedAppointment} />
            <AppointmentDetails appointment={selectedAppointment} />
            <AppointmentActions
              appointment={selectedAppointment}
              onUpdate={handleAppointmentUpdate}
            />
          </>
        )}
      </div>
    </div>
  );
};

export default DoctorDashboard;
